INSERT INTO `khoa` (`id_khoa`, `tenkhoa`, `hinh`, `mota`) VALUES
(1, 'Khoa nội tim mạch tổng quát (Nội tim mạch 3)', 'ntm1.png', '<p class=\"p1\">Bệnh Tim mạch thường gặp ở người lớn tuổi, v&agrave; đi k&egrave;m nhiều bệnh l&yacute; phối hợp phức tạp. Đ&acirc;y l&agrave; l&iacute; do&nbsp;<strong>Khoa Nội Tim Mạch Tổng Qu&aacute;t</strong>&nbsp;đ&atilde; ra đời<span class=\"Apple-converted-space\">&nbsp;</span>ngay từ khi th&agrave;nh lập bệnh viện. Nơi đ&acirc;y tập trung những B&aacute;c sĩ Tim mạch<span class=\"Apple-converted-space\">&nbsp;</span>gi&agrave;u<span class=\"Apple-converted-space\">&nbsp;</span>kinh nghiệm v&agrave; c&oacute; bằng<span class=\"Apple-converted-space\">&nbsp;</span>sau Đại học về Nội Tổng Qu&aacute;t.</p>\r\n<p class=\"p1\">Lu&ocirc;n tu&acirc;n thủ theo c&aacute;c cập nhật mới nhất của c&aacute;c hướng dẫn quốc tế v&agrave; với những trang thiết bị hiện đại, ch&uacute;ng t&ocirc;i &aacute;p dụng để chẩn đo&aacute;n những trường hợp kh&oacute; bằng c&aacute;c thử nghiệm như si&ecirc;u &acirc;m tim gắng sức, điện t&acirc;m đồ gắng sức, chụp cắt lớp điện to&aacute;n để t&igrave;m bệnh Động mạch v&agrave;nh tiềm ẩn. Để đ&aacute;nh gi&aacute; ch&iacute;nh x&aacute;c tổn thương bệnh van tim, bệnh Động mạch chủ ngực nhằm đưa ra điều trị th&iacute;ch hợp nhất, ngo&agrave;i si&ecirc;u &acirc;m tim Doppler m&agrave;u, ch&uacute;ng t&ocirc;i phối hợp ph&ograve;ng kh&aacute;m Ph&uacute;c Đức thực hiện nội soi tại chỗ. Ngo&agrave;i ra, để t&igrave;m nguy&ecirc;n nh&acirc;n xuất huyết ti&ecirc;u ho&aacute;, ch&uacute;ng t&ocirc;i phối hợp với Ph&ograve;ng kh&aacute;m Ph&uacute;c Đức để thực hiện nội soi tại chỗ.</p>\r\n<p class=\"p1\">Ch&uacute;ng t&ocirc;i lu&ocirc;n tập trung hết nh&acirc;n lực v&agrave; vật tư để t&igrave;m nguy&ecirc;n nh&acirc;n, đ&aacute;nh gi&aacute; đ&uacute;ng t&igrave;nh trạng bệnh, &aacute;p dụng phương ph&aacute;p điều trị th&iacute;ch hợp nhất để bệnh nh&acirc;n hồi phục sớm nhất v&agrave; r&uacute;t ngắn thời gian nằm viện.</p>\r\n<p class=\"p1\" style=\"text-align: left;\"><strong>Mục ti&ecirc;u của ch&uacute;ng t&ocirc;i l&agrave; điều trị v&agrave; chăm s&oacute;c to&agrave;n diện cho những bệnh nh&acirc;n mang nhiều bệnh phối hợp, phức tạp.</strong></p>\r\n<p class=\"p1\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/KNTMTQ-Gioi-thieu-2.jpg\" width=\"452\" height=\"300\"></p>'),
(2, 'Khoa phẫu thuật tim', 'ntm2.png', '<p>Từ năm 2006,&nbsp;<strong>Khoa Phẫu thuật Tim</strong> Bệnh viện Tim T&acirc;m Đức được th&agrave;nh lập tr&ecirc;n nền tảng từ c&aacute;c Phẫu thuật vi&ecirc;n Tim mạch của Viện Tim Th&agrave;nh Phố Hồ Ch&iacute; Minh v&agrave; c&aacute;c phẫu thuật vi&ecirc;n được đ&agrave;o tạo b&agrave;i bản từ Viện Tim, thừa hưởng tinh thần v&agrave; kỹ thuật của Viện Tim, kinh nghiệm mổ tim của Ph&aacute;p, do Gs Alain Carpentier v&agrave; GS. Alain Deloche truyền thụ.</p>\r\n<p class=\"p1\">Gắn liền với hoạt động phẫu thuật tim, Bệnh viện Tim T&acirc;m Đức đ&atilde; thực hiện c&aacute;c ca phẫu thuật bệnh l&yacute; mạch m&aacute;u với tỉ lệ th&agrave;nh c&ocirc;ng tr&ecirc;n 98%.&nbsp;Từ 2019 Bệnh viện Tim T&acirc;m Đức đ&atilde; triển khai thực hiện phẫu thuật tim &iacute;t x&acirc;m lấn cho c&aacute;c trường hợp bệnh l&yacute; van tim v&agrave; tim bẩm sinh kh&ocirc;ng phức tạp nhằm r&uacute;t ngắn thời gian hồi phục sau mổ v&agrave; đ&aacute;p ứng nhu cầu thẩm mỹ cho bệnh nh&acirc;n.</p>\r\n<p class=\"p1\">Bệnh viện Tim T&acirc;m Đức đ&atilde; bắt đầu hoạt động chương tr&igrave;nh phẫu thuật tim cho c&aacute;c trẻ em ngh&egrave;o mắc bệnh tim. Từ ng&agrave;y bắt đầu hoạt động đến nay, đ&atilde; phẫu thuật tim cho hơn 6.000 trường hợp c&oacute; t&agrave;i trợ từ c&aacute;c tổ chức từ thiện trong v&agrave; ngo&agrave;i nước.</p>\r\n<p class=\"p1\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/PTT-Gioi-thieu-2.jpg\" width=\"580\" height=\"386\"></p>\r\n<p class=\"p1\">Đội ngũ y B&aacute;c sĩ Bệnh viện Tim T&acirc;m Đức lu&ocirc;n c&oacute; sự hướng dẫn của c&aacute;c chuy&ecirc;n gia h&agrave;ng đầu của cả nước v&agrave; c&aacute;c trung t&acirc;m tim mạch nổi tiếng tr&ecirc;n thế giới như: Trung t&acirc;m tim mạch George Pompidou &ndash; Paris, Trung t&acirc;m tim mạch quốc gia IJN-Malaysia, ĐH quốc gia Singapore NUS, Boston Children Hospital-US, Bệnh viện Necker Des Enfants Malades của Paris &hellip; để hội chẩn những trường hợp bệnh phức tạp.</p>\r\n<p class=\"p1\">Đội ngũ Phẫu thuật vi&ecirc;n Tim mạch của Bệnh viện Tim T&acirc;m Đức&nbsp;c&oacute; nền tảng y học vững v&agrave;ng, nhiệt t&igrave;nh, phối hợp với c&aacute;c đồng nghiệp rất hiệu quả, đảm bảo t&iacute;nh sẵn s&agrave;ng, chuy&ecirc;n nghiệp cao v&agrave; lu&ocirc;n đặt mục ti&ecirc;u chất lượng phục vụ bệnh nh&acirc;n l&ecirc;n h&agrave;ng đầu.<strong><em>&nbsp;</em></strong></p>\r\n<p>&nbsp;</p>'),
(3, 'Khoa phòng khám', 'kiemsoatnhiemkhuan.jpg', '<p><strong>Khoa&nbsp;</strong><strong>Ph&ograve;ng Kh&aacute;m</strong>&nbsp;Bệnh viện Tim T&acirc;m Đức hoạt động từ&nbsp;những ng&agrave;y đầu th&agrave;nh lập bệnh viện, đến nay đ&atilde; mở rộng quy m&ocirc; th&agrave;nh 2 khu kh&aacute;m với 15 ph&ograve;ng kh&aacute;m bệnh v&agrave; c&aacute;c ph&ograve;ng chức năng thực hiện c&aacute;c cận l&acirc;m s&agrave;ng. Tr&ecirc;n tinh thần l&agrave;m việc lấy chất lượng chuy&ecirc;n m&ocirc;n l&agrave; mục ti&ecirc;u h&agrave;ng đầu, đội ngũ b&aacute;c sĩ ch&uacute;ng t&ocirc;i kh&ocirc;ng ngừng cập nhật v&agrave; &aacute;p dụng những tiến bộ y học mới nhất trong chẩn đo&aacute;n v&agrave; điều trị bệnh tim mạch.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/PK-Gioi-thieu-2.jpg\" width=\"554\" height=\"369\"></p>\r\n<p>Với định hướng ph&aacute;t triển chuy&ecirc;n s&acirc;u tim mạch theo ti&ecirc;u chuẩn quốc tế, hiện tại khoa Kh&aacute;m Bệnh ngoại tr&uacute; l&agrave; đơn vị ti&ecirc;n phong ph&aacute;t triển c&aacute;c ph&ograve;ng kh&aacute;m chuy&ecirc;n s&acirc;u như ph&ograve;ng kh&aacute;m bệnh cơ tim, kh&aacute;ng đ&ocirc;ng, rung nhĩ, suy tim, tăng huyết &aacute;p, tim bẩm sinh v&agrave; tăng &aacute;p động mạch phổi.</p>\r\n<p>Tại đ&acirc;y, ch&uacute;ng t&ocirc;i tiếp nhận v&agrave; thăm kh&aacute;m tất cả bệnh nh&acirc;n tim mạch trẻ em v&agrave; người lớn. B&ecirc;n cạnh đ&oacute;, ph&ograve;ng kh&aacute;m c&oacute; đầy đủ trang thiết bị chuy&ecirc;n s&acirc;u tim mạch hỗ trợ chẩn đo&aacute;n v&agrave; điều trị hiệu quả.</p>'),
(4, 'Khoa điện sinh lý và loạn nhịp tim (Nội tim mạch 1)', 'ntm4.jpg', '<p><strong>Khoa Điện Sinh L&yacute; v&agrave; Tạo Nhịp Tim</strong>&nbsp;được th&agrave;nh&nbsp;lập v&agrave;o th&aacute;ng 1/2008 nhằm mục đ&iacute;ch điều trị chuy&ecirc;n s&acirc;u c&aacute;c trường hợp rối loạn nhịp tim. T&iacute;nh đến nay<span class=\"s1\">, sau 10 năm hoạt động khoa đ&atilde; điều trị th&agrave;nh c&ocirc;ng h&agrave;ng ng&agrave;n ca rối loạn nhịp từ đơn giản đến phức tạp.&nbsp;</span>Từ th&aacute;ng 2/2017 Khoa đ&atilde; &aacute;p dụng th&agrave;nh c&ocirc;ng kỹ thuật cắt đốt điện sinh l&yacute; c&oacute; sử dụng hệ thống lập bản đồ 3D (kh&ocirc;ng gian 3 chiều) để điều trị c&aacute;c loại rối loạn nhịp phức tạp như rung nhĩ, cuồng nhĩ, nhịp nhanh thất li&ecirc;n quan sẹo cơ tim, nhịp nhanh thất tr&ecirc;n bệnh nh&acirc;n c&oacute; hội chứng Brugada&hellip; C&aacute;c kết quả điều trị<span class=\"Apple-converted-space\">&nbsp;</span>của Khoa được b&aacute;o c&aacute;o ở c&aacute;c hội nghị trong nước v&agrave; quốc tế đ&atilde; ghi nhận những nỗ lực của đội ngũ trong việc kh&ocirc;ng ngừng n&acirc;ng cao chất lượng chuy&ecirc;n m&ocirc;n.</p>\r\n<p style=\"text-align: center;\"><img src=\"https://tamduchearthospital.com/wp-content/uploads/2019/07/Dien-Sinh-Ly-Loan-Nhip-Tim-Noi-1-2-640x320-1.png\"></p>'),
(5, 'Khoa thông tim can thiệp (Nội tim mạch 2)', 'ntm2.png', '<p class=\"p1\"><strong>Khoa Th&ocirc;ng Tim Can Thiệp</strong>, với đội ngũ B&aacute;c sĩ được đ&agrave;o tạo trong v&agrave; ngo&agrave;i nước (Malaysia, Nhật, Thuỵ Sỹ&hellip;), v&agrave; trang thiết bị hiện đại (m&aacute;y DSA Philips Allura Xper FD10, m&aacute;y si&ecirc;u &acirc;m trong l&ograve;ng mạch IVUS, m&aacute;y đo ph&acirc;n suất dự trữ v&agrave;nh FFR/iFR,&hellip;) thực hiện th&agrave;nh c&ocirc;ng hầu hết c&aacute;c trường hợp th&ocirc;ng tim chẩn đo&aacute;n v&agrave; can thiệp bệnh mạch v&agrave;nh, bệnh mạch m&aacute;u ngoại bi&ecirc;n, bệnh van tim, bệnh cấu tr&uacute;c tim, bệnh tim bẩm sinh.</p>\r\n<p class=\"p1\" style=\"text-align: center;\"><img src=\"https://tamduchearthospital.com/wp-content/uploads/2019/03/Thong-Tim-Can-Thiep-Noi-2-640-x-320.png\"></p>\r\n<p class=\"p1\">Dựa tr&ecirc;n những khuyến c&aacute;o thực h&agrave;nh mới nhất của Trường m&ocirc;n Tim mạch / Hội tim mạch Hoa Kỳ (ACC/AHA), Hội tim mạch Ch&acirc;u &Acirc;u (ESC), Hội Tim mạch Việt Nam (VNHA), kết hợp với kinh nghiệm l&acirc;m s&agrave;ng của đội ngũ b&aacute;c sĩ điều tri, ch&uacute;ng t&ocirc;i sẵn s&agrave;ng tư vấn, gi&uacute;p đưa ra hướng điều trị ph&ugrave; hợp nhất cho từng bệnh nh&acirc;n cụ thể.</p>'),
(6, 'Khoa bệnh lý mạch máu', 'benhlymachmau.jpg', '<p>Được x&acirc;y dựng với mục đ&iacute;ch ho&agrave;n thiện hoạt động Phẫu thuật tim &ndash; mạch của Bệnh viện Tim T&acirc;m Đức, khoa&nbsp;<strong>Can Thiệp Mạch M&aacute;u</strong>&nbsp;của ch&uacute;ng t&ocirc;i đ&atilde; được th&agrave;nh lập v&agrave;o năm 2011.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/03/KBLMM-Gioi-thieu.jpg\" width=\"621\" height=\"414\"></p>\r\n<p><span class=\"s1\">Với phương ch&acirc;m&nbsp;<em><strong>&ldquo;Chất lượng Quốc tế v&igrave; Tr&aacute;i tim Việt&rdquo;</strong></em>, từ nhiều năm qua với những nỗ lực kh&ocirc;ng ngừng c&ugrave;ng sự t&iacute;n nhiệm của người bệnh, đội ngũ chuy&ecirc;n gia của ch&uacute;ng t&ocirc;i đ&atilde; chẩn đo&aacute;n v&agrave; điều trị can thiệp th&agrave;nh c&ocirc;ng rất nhiều trường hợp bệnh l&yacute; phức tạp</span>, cần được điều trị bằng phương ph&aacute;p phẫu thuật hở, hoặc phải tốn k&eacute;m rất nhiều tiền bạc để thực hiện can thiệp tại Singapore, Th&aacute;i Lan, M&atilde; Lai,&hellip; như bệnh mạch v&agrave;nh phức tạp, bệnh mạch m&aacute;u ngoại bi&ecirc;n như động mạch cảnh, động mạch chậu, động mạch thận, hay bệnh động mạch chủ như ph&igrave;nh b&oacute;c t&aacute;ch động mạch chủ ngực, động mạch chủ bụng, hẹp eo động mạch chủ,&hellip;cho đến c&aacute;c bệnh tim cấu tr&uacute;c như thay van động mạch chủ qua da (TAVI).</p>'),
(7, 'Khoa hồi sức cấp cứu nội tim mạch (USIC)', 'hoisuccapcuuNTM.jpg', '<p class=\"p1\"><strong>Khoa Hồi Sức<span class=\"Apple-converted-space\">&nbsp;</span>Nội Tim Mạch</strong>&nbsp;Bệnh viện Tim T&acirc;m Đức&nbsp;th&agrave;nh lập từ năm 2006, hoạt động 24/24 v&agrave;o tất cả c&aacute;c ng&agrave;y trong tuần, kể cả ng&agrave;y lễ.</p>\r\n<p class=\"p2\">Đội ngũ b&aacute;c sĩ, điều dưỡng tại khoa Hồi sức Nội tim mạch được đ&agrave;o tạo li&ecirc;n tục, c&oacute; thể tiếp nhận v&agrave; xử tr&iacute; khẩn cấp c&aacute;c trường hợp cấp cứu bệnh nh&acirc;n v&agrave; c&oacute; sự phối hợp với tất c&aacute;c chuy&ecirc;n khoa của Bệnh viện nhanh ch&oacute;ng, kịp thời<span class=\"s1\">&nbsp;(như khoa phẫu thuật tim, loạn nhịp, th&ocirc;ng tim, nội tiết&hellip;).</span></p>\r\n<p class=\"p2\"><span class=\"s1\"><img src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/DSCF3330-300x200.jpg\" width=\"310\" height=\"206\"></span></p>\r\n<p class=\"p1\">Qua 12 năm hoạt động, khoa Hồi sức Nội tim mạch đ&atilde; thực hiện hồi sức v&agrave; cấp cứu cho tr&ecirc;n 12.000 bệnh nh&acirc;n nặng với tỉ lệ hồi sức th&agrave;nh c&ocirc;ng &gt; 95%.</p>\r\n<p class=\"p1\">Khoa Hồi sức Nội tim mạch c&ograve;n ki&ecirc;m nhiệm hỗ trợ c&aacute;c khoa ph&ograve;ng trong cấp cứu t&igrave;nh huống khẩn cấp theo qui tr&igrave;nh B&aacute;o động đỏ nội viện,&nbsp;<strong>đảm bảo nhanh ch&oacute;ng &ndash; kịp thời &ndash; an to&agrave;n &ndash; hiệu quả l&agrave; phương ch&acirc;m của khoa</strong>. Đặc biệt trong năm 2018, Khoa đ&atilde; thực hiện 26 trường hợp b&aacute;o động đỏ nội viện với tỉ lệ cấp cứu &ndash; hồi sức th&agrave;nh c&ocirc;ng 25/26 (&gt; 96%).</p>\r\n<p class=\"p1\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/HSNTM-Gioi-thieu-2.jpg\" width=\"641\" height=\"427\"></p>\r\n<p class=\"p1\">Điểm nổi bật của Khoa Hồi sức Cấp cứu l&agrave; đội cấp cứu ngoại viện lu&ocirc;n lu&ocirc;n trực 24/24 sẵn s&agrave;ng tiếp nhận, vận chuyển bệnh, xử tr&iacute; cấp cứu cả nội th&agrave;nh<span class=\"Apple-converted-space\">&nbsp;&nbsp;</span>v&agrave; ngoại th&agrave;nh<span class=\"Apple-converted-space\">&nbsp;&nbsp;</span>(tại nh&agrave; ri&ecirc;ng hoặc từ c&aacute;c cơ sở y tế kh&aacute;c) với đội ngũ chuy&ecirc;n nghiệp v&agrave; đầy đủ phương tiện cấp cứu như 1 ph&ograve;ng hồi sức di động.</p>'),
(8, 'Khoa tim mạch chuyển hóa (Nội tim mạch 4)', 'ntm4.jpg', '<p>Đội ngũ của khoa&nbsp;<strong>Tim Mạch Chuyển H&oacute;a</strong>&nbsp;bao gồm những B&aacute;c sĩ được đ&agrave;o tạo chuy&ecirc;n s&acirc;u về Tim mạch, nội tiết v&agrave; dinh dưỡng. Ch&uacute;ng t&ocirc;i quản l&yacute;, chẩn đo&aacute;n v&agrave; điều trị những bệnh nh&acirc;n c&oacute; c&aacute;c yếu tố nguy cơ tim mạch chuyển h&oacute;a như đ&aacute;i th&aacute;o đường, tăng huyết &aacute;p, rối loạn lipid m&aacute;u, b&eacute;o ph&igrave;&hellip; cũng như những bệnh l&yacute; tim mạch li&ecirc;n quan đến c&aacute;c rối loạn n&agrave;y như suy tim, bệnh động mạch v&agrave;nh, bệnh động mạch ngoại bi&ecirc;n&hellip;</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/KTMCH-Gioi-thieu-1.jpg\" width=\"602\" height=\"401\"></p>\r\n<p>Ngo&agrave;i ra, ch&uacute;ng t&ocirc;i c&ograve;n điều trị những bệnh nh&acirc;n bệnh cơ tim. Nhiều bệnh nh&acirc;n bệnh cơ tim ph&igrave; đại, d&atilde;n nở, hạn chế đ&atilde; được điều trị với kết quả tốt tại đ&acirc;y theo những phương ph&aacute;p hiện đại bao gồm nội khoa, can thiệp v&agrave; ngoại khoa. Đặc biệt, ch&uacute;ng t&ocirc;i đ&atilde; ph&aacute;t hiện nhiều bệnh cơ tim, rối loạn chuyển h&oacute;a di truyền v&agrave; chăm s&oacute;c, tư vấn cho cả những người th&acirc;n trong gia đ&igrave;nh của họ. Ch&uacute;ng t&ocirc;i đ&atilde; x&acirc;y dựng th&agrave;nh c&ocirc;ng m&ocirc; h&igrave;nh chăm s&oacute;c to&agrave;n diện bệnh cơ tim di truyền từ chẩn đo&aacute;n, điều trị, đến tư vấn di truyền bệnh cơ tim.</p>'),
(9, 'Khoa phục hồi chức năng tim mạch (Nội tim mạch 5)', 'ntm5.jpg', '<p>Nếu bạn vừa được chẩn đo&aacute;n mắc bệnh tim mạch v&agrave; cần 1 hệ thống hỗ trợ?&nbsp;Ch&uacute;ng t&ocirc;i đang ở ngay b&ecirc;n cạnh bạn.&nbsp;<strong>Khoa Phục Hồi Chức Năng Tim Mạch&nbsp;</strong>Bệnh viện Tim T&acirc;m Đức th&agrave;nh lập năm 2011, lu&ocirc;n sẵn s&agrave;ng to&agrave;n t&acirc;m to&agrave;n &yacute; để gi&uacute;p bạn hồi phục v&agrave; trở lại với c&aacute;c hoạt động hằng ng&agrave;y m&agrave; bạn y&ecirc;u th&iacute;ch. Ch&uacute;ng t&ocirc;i c&oacute; thể hướng dẫn bạn c&aacute;ch thay đổi lối sống l&agrave;m cải thiện sức khoẻ, c&aacute;ch để ổn định, l&agrave;m chậm v&agrave; gi&uacute;p bạn phục hồi lại sức khỏe sau cơn đau tim. Bệnh tim tuy rất đ&aacute;ng sợ, nhưng bạn kh&ocirc;ng phải chiến đấu một m&igrave;nh v&igrave; ch&uacute;ng t&ocirc;i lu&ocirc;n b&ecirc;n cạnh bạn.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/03/KPHCNTM-Gioi-thieu.jpg\" width=\"590\" height=\"393\"></p>'),
(10, 'Khoa hồi sức cấp cứu ngoại tim mạch', 'Hoisuccapcuungoaitimmach.png', '<p>Phẫu thuật tim l&agrave; một ng&agrave;nh chuy&ecirc;n khoa s&acirc;u, v&igrave; thế rất cần sự phối hợp l&agrave;m việc của nhiều nh&oacute;m chuy&ecirc;n nghiệp được đ&agrave;o tạo kỹ lưỡng, gồm B&aacute;c sĩ Nội Tim mạch chuẩn bị bệnh nh&acirc;n trước mổ, Phẫu thuật vi&ecirc;n, B&aacute;c sĩ G&acirc;y m&ecirc;, B&aacute;c sĩ tim phổi nh&acirc;n tạo, B&aacute;c sĩ hồi sức. Trong đ&oacute;, giai đoạn hồi sức rất quan trọng n&ecirc;n cần một đơn vị với nh&acirc;n vi&ecirc;n được đ&agrave;o tạo chuy&ecirc;n s&acirc;u v&agrave; trang thiết bị hiện đại.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/Gioi-thieu-Xoa-giup-vet-do-tren-mat-em-be.jpg\" width=\"651\" height=\"434\"></p>\r\n<p class=\"p1\">Ch&uacute;ng t&ocirc;i điều trị Hồi sức sau phẫu thuật chủ yếu 3 nh&oacute;m bệnh&nbsp;bao gồm Tim bẩm sinh, Phẫu thuật bắc cầu động mạch v&agrave;nh v&agrave; Phẫu thuật van tim.</p>\r\n<p class=\"p2\">Với trang thiết bị hiện đại như monitor theo d&otilde;i huyết động li&ecirc;n tục, m&aacute;y đo cung lượng tim 24 giờ, m&aacute;y đối xung nội động mạch chủ, m&aacute;y thở hiện đại, m&aacute;y lọc m&aacute;u li&ecirc;n tục, m&aacute;y hạ th&acirc;n nhiệt,m&aacute;y sưởi ấm, giường sưởi, ch&uacute;ng t&ocirc;i lu&ocirc;n hỗ trợ bệnh nh&acirc;n tối ưu để hồi phục sau phẫu thuật.</p>'),
(11, 'Khoa gây mê', 'gayme.jpg', '<p>Tr&acirc;n trọng c&aacute;m ơn bạn đ&atilde; gh&eacute; thăm khoa G&acirc;y m&ecirc; Hồi sức ch&uacute;ng t&ocirc;i.&nbsp;Th&agrave;nh lập từ 2006,&nbsp;<strong>Khoa G&acirc;y M&ecirc;</strong>&nbsp;l&agrave;m việc dựa tr&ecirc;n ti&ecirc;u ch&iacute;&nbsp;<strong><em>&ldquo;</em></strong><strong><em>an to&agrave;n</em></strong><strong><em>&nbsp;bệnh nh&acirc;n tr&ecirc;n hết&rdquo;</em></strong><strong>.</strong>&nbsp;Ch&uacute;ng t&ocirc;i quản l&yacute; v&agrave; hỗ trợ chăm s&oacute;c 3 ph&ograve;ng phẫu thuật tim, 2 ph&ograve;ng Th&ocirc;ng tim can thiệp, 1 ph&ograve;ng Khảo s&aacute;t điện sinh l&yacute; tim v&agrave; tạo nhịp, 1 ph&ograve;ng MSCT. H&agrave;ng năm, ch&uacute;ng t&ocirc;i g&acirc;y m&ecirc; v&agrave; hồi sức gần 2000 BN phẫu thuật tim v&agrave; th&ocirc;ng tim nhằm giảm lo lắng, căng thẳng cho bệnh nh&acirc;n, l&agrave;m mất cảm gi&aacute;c đau trong l&uacute;c phẫu thuật, gi&uacute;p bệnh nh&acirc;n nhanh ch&oacute;ng phục hồi sau phẫu thuật. C&aacute;c b&aacute;c sĩ, điều dưỡng v&agrave; kỹ thuật vi&ecirc;n g&acirc;y m&ecirc; trong khoa sử dụng những phương ph&aacute;p v&ocirc; cảm &iacute;t t&aacute;c dụng phụ nhất, sử dụng thuốc v&agrave; phương tiện hiện đại, đồng thời tu&acirc;n thủ chặt chẽ c&aacute;c quy tr&igrave;nh kỹ thuật.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/03/Gioi-thieu.jpg\" width=\"588\" height=\"392\"></p>\r\n<p>Từ năm 2019, khoa G&acirc;y m&ecirc; &aacute;p dụng kỹ thuật g&acirc;y t&ecirc; ngo&agrave;i m&agrave;ng cứng phối hợp để giảm đau cho c&aacute;c bệnh nh&acirc;n sau phẫu thuật. Khoa kh&ocirc;ng ngừng ho&agrave;n thiện về kỹ thuật, n&acirc;ng cao về chuy&ecirc;n m&ocirc;n bằng c&aacute;c&nbsp;chương tr&igrave;nh đ&agrave;o tạo huấn luyện trong v&agrave; ngo&agrave;i nước nhằm đ&aacute;p ứng đầy đủ nhu cầu Phẫu thuật tim.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, ch&uacute;ng t&ocirc;i cũng hợp t&aacute;c v&agrave; chuyển giao kỹ thuật trong lĩnh vực G&acirc;y m&ecirc; Hồi sức Phẫu thuật Tim mạch cho c&aacute;c bệnh viện bạn trong khu vực (Campuchia, L&agrave;o).</p>'),
(12, 'Khoa phòng mổ', 'phongmo.jpg', '<p><strong>Khoa Ph&ograve;ng Mổ</strong>&nbsp;Bệnh viện Tim T&acirc;m Đức được th&agrave;nh lập từ năm 2006. Đội ngũ bao gồm c&aacute;c Phẫu thuật vi&ecirc;n xuất th&acirc;n hoặc được đ&agrave;o tạo từ Viện Tim v&agrave; nước ngo&agrave;i với nhiều năm kinh nghiệm trong lĩnh vực phẫu thuật Tim mạch. Đặc biệt c&oacute; Thạc sĩ. B&aacute;c sĩ Phan Kim Phương, nguy&ecirc;n Gi&aacute;m đốc Viện Tim th&agrave;nh phố Hồ Ch&iacute; Minh trực tiếp hội chẩn, tư vấn v&agrave; phẫu thuật.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/BS.-Hiep-phau-thuat.jpg\" width=\"570\" height=\"382\"></p>\r\n<p>Ch&uacute;ng t&ocirc;i lu&ocirc;n cập nhật v&agrave; phối hợp với c&aacute;c chuy&ecirc;n gia nước ngo&agrave;i, thực hiện c&aacute;c phương ph&aacute;p phẫu thuật ti&ecirc;n tiến nhất để đ&aacute;p ứng nhu cầu của bệnh nh&acirc;n v&agrave; sự ph&aacute;t triển của y học Việt Nam v&agrave; Thế giới. Phẫu thuật sửa van 2 l&aacute; theo kỹ thuật Carpentier l&agrave; kỹ thuật ưu việt trong điều trị bệnh l&yacute; van 2 l&aacute; được xem l&agrave; kỹ thuật ch&iacute;nh thống nhất tại Việt Nam. Từ th&aacute;ng 12/2018 ch&uacute;ng t&ocirc;i đ&atilde; triển khai th&ecirc;m phẫu thuật thay van động mạch chủ kh&ocirc;ng chỉ kh&acirc;u nhằm r&uacute;t ngắn thời gian phẫu thuật v&agrave; gi&uacute;p bệnh nh&acirc;n hồi phục nhanh sau phẫu thuật.</p>'),
(13, 'Khoa chẩn đoán hình ảnh', 'chuandoanhinhanh.png', '<p class=\"p1\"><strong>Khoa Chẩn Đo&aacute;n H&igrave;nh ảnh</strong>&nbsp;Bệnh viện Tim T&acirc;m Đức cung cấp c&aacute;c dịch vụ chẩn đo&aacute;n h&igrave;nh ảnh hiện đại v&agrave; bao qu&aacute;t để gi&uacute;p chẩn đo&aacute;n v&agrave; g&oacute;p phần v&agrave;o điều trị của bạn.</p>\r\n<p class=\"p1\">Tất cả c&aacute;c chẩn đo&aacute;n h&igrave;nh ảnh của ch&uacute;ng t&ocirc;i đều sử dụng c&ocirc;ng nghệ h&igrave;nh ảnh ti&ecirc;n tiến v&agrave; tu&acirc;n thủ c&aacute;c ti&ecirc;u chuẩn quy định nghi&ecirc;m ngặt. Đội ngũ chuy&ecirc;n gia h&igrave;nh ảnh học của ch&uacute;ng t&ocirc;i bao gồm c&aacute;c b&aacute;c sĩ v&agrave; kỹ thuật vi&ecirc;n nhiều kinh nghiệm v&agrave; được đ&agrave;o tạo chuy&ecirc;n s&acirc;u trong v&agrave; ngo&agrave;i nước, sẽ thực hiện v&agrave; ph&acirc;n t&iacute;ch c&aacute;c chẩn đo&aacute;n h&igrave;nh ảnh với độ ch&iacute;nh x&aacute;c tối đa để bạn nhận được kết quả ch&iacute;nh x&aacute;c nhất.</p>\r\n<p class=\"p1\" style=\"text-align: center;\"><img src=\"https://tamduchearthospital.com/wp-content/uploads/2019/03/Chan-Doan-Hinh-Anh-640x320-1.png\"></p>\r\n<p class=\"p1\">Tất cả m&aacute;y chụp X-quang đều l&agrave; m&aacute;y kỹ thuật số, từ m&aacute;y cố định chụp tại khoa Chẩn đo&aacute;n h&igrave;nh ảnh đến m&aacute;y di động chụp tại giường. Điều n&agrave;y bảo đảm bệnh nh&acirc;n ch&uacute;ng t&ocirc;i sẽ được phục vụ tốt hơn với:</p>\r\n<ul class=\"ul1\">\r\n<li class=\"li1 first-child\">Tăng sự thoải m&aacute;i cho bệnh nh&acirc;n.</li>\r\n<li class=\"li1\">Giảm tiếp x&uacute;c với bức xạ.</li>\r\n<li class=\"li1\">Cải thiện chất lượng h&igrave;nh ảnh.</li>\r\n<li class=\"li1 last-child\">Khả năng gởi, lưu h&igrave;nh ảnh v&agrave; hội chẩn dễ d&agrave;ng.Tam Duc Hospital. &copy; Pascal Deloche</li>\r\n</ul>'),
(14, 'Khoa kiểm soát nhiễm khuẩn', 'kiemsoatnhiemkhuan.jpg', '<p class=\"p1\">Ph&ograve;ng ngừa v&agrave; kiểm so&aacute;t nhiễm khuẩn li&ecirc;n quan đến chăm s&oacute;c Y tế lu&ocirc;n l&agrave; ưu ti&ecirc;n h&agrave;ng đầu của Bệnh viện Tim T&acirc;m Đức.</p>\r\n<p class=\"p1\">Mục đ&iacute;ch ch&uacute;ng t&ocirc;i l&agrave; giảm nguy cơ l&acirc;y nhiễm th&ocirc;ng qua truyền th&ocirc;ng gi&aacute;o dục, kiểm tra gi&aacute;m s&aacute;t c&aacute;c quy tr&igrave;nh thực h&agrave;nh, sự cam kết v&agrave; đồng thuận của tất cả c&aacute;c nh&acirc;n vi&ecirc;n v&agrave; theo Hướng dẫn của Bộ Y tế.</p>\r\n<p class=\"p1\"><strong>Khoa Kiểm so&aacute;t nhiễm khuẩn</strong> tham gia v&agrave;o c&aacute;c chương tr&igrave;nh quốc gia để giảm thiểu c&aacute;c bệnh nhiễm tr&ugrave;ng li&ecirc;n quan đến chăm s&oacute;c sức khỏe.</p>\r\n<p class=\"p1\">Sự kết nối giữa tất cả Nh&acirc;n vi&ecirc;n Y tế, với bệnh nh&acirc;n, cũng như kh&aacute;ch đến thăm nhằm đảm bảo kiểm so&aacute;t v&agrave; ph&ograve;ng ngừa nhiễm tr&ugrave;ng hiệu quả.</p>'),
(15, 'Khoa dược', 'duoc.jpg', '<p><span class=\"s1\"><strong>Khoa Dược</strong>&nbsp;th&agrave;nh lập năm 2006, l&agrave; một khoa chuy&ecirc;n m&ocirc;n chịu sự l&atilde;nh đạo trực tiếp của Gi&aacute;m Đốc Bệnh Viện, quản l&yacute; v&agrave; tham mưu cho Gi&aacute;m Đốc Bệnh Viện về to&agrave;n bộ c&ocirc;ng t&aacute;c dược.&nbsp;</span>Với phương ch&acirc;m&nbsp;<strong>&ldquo;<em>bệnh nh&acirc;n l&agrave;m trung t&acirc;m&rdquo;</em></strong>, ch&uacute;ng t&ocirc;i đồng h&agrave;nh với đội ngũ nh&acirc;n vi&ecirc;n Y tế lu&ocirc;n hướng tới việc bảo đảm quản l&yacute; cung ứng v&agrave; sử dụng thuốc, vật tư y tế an to&agrave;n, hiệu quả , hợp l&yacute;, tu&acirc;n thủ c&aacute;c quy tr&igrave;nh chuy&ecirc;n m&ocirc;n theo đ&uacute;ng quy định Bộ Y tế.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/03/Gioi-thieu-3.jpg\" width=\"596\" height=\"397\"></p>'),
(16, 'Khoa xét nghiệm', 'xetnghiem.jpg', '<p>Với mục ti&ecirc;u cung cấp một dịch vụ chất lượng cho bệnh nh&acirc;n, mang đến kết quả ch&iacute;nh x&aacute;c nhất trong thời gian ngắn nhất, g&oacute;p phần v&agrave;o c&ocirc;ng t&aacute;c chẩn đo&aacute;n v&agrave; điều trị của B&aacute;c sĩ l&acirc;m s&agrave;ng, ch&uacute;ng t&ocirc;i cung cấp dịch vụ 24/7 với việc kh&ocirc;ng ph&aacute;t sinh th&ecirc;m chi ph&iacute; ngo&agrave;i giờ h&agrave;nh ch&aacute;nh v&agrave; tất cả kết quả đều theo ti&ecirc;u chuẩn quốc tế.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2019/05/KXN-Gioi-thieu.jpg\" width=\"687\" height=\"458\"></p>\r\n<p class=\"p1\">Với ti&ecirc;u ch&iacute;&nbsp;<strong><em>&ldquo;Nhanh ch&oacute;ng &ndash; Kịp thời &ndash; Ch&iacute;nh x&aacute;c&rdquo;,&nbsp;</em></strong>ch&uacute;ng t&ocirc;i mong muốn đem lại một dịch vụ chăm s&oacute;c bảo đảm an to&agrave;n v&agrave; thoải m&aacute;i cho bệnh nh&acirc;n.</p>\r\n<p class=\"p2\"><span class=\"s1\">Với&nbsp;</span>đội ngũ c&aacute;n bộ c&oacute; nhiều năm kinh nghiệm, với hệ thống trang thiết bị&nbsp;<span class=\"s1\">tự động&nbsp;</span>hiện đại ch&uacute;ng t&ocirc;i đ&atilde; thực hiện hơn 600.000 trường hợp x&eacute;t nghiệm cho mỗi năm.</p>'),
(17, 'Khoa dinh dưỡng', 'dinhduong.jpg', '<p>Dinh dưỡng đ&oacute;ng vai tr&ograve; quan trọng trong sức khỏe tim mạch, gi&uacute;p bạn ngăn ngừa v&agrave; cũng g&oacute;p phần đảo ngược c&aacute;c vấn đề về tim mạch. Do đ&oacute;,&nbsp;<strong>Khoa Dinh Dưỡng</strong>&nbsp;được th&agrave;nh lập v&agrave; đồng h&agrave;nh c&ugrave;ng c&aacute;c khoa điều trị Bệnh viện Tim T&acirc;m Đức nhằm hướng tới việc chăm s&oacute;c to&agrave;n diện bệnh nh&acirc;n tim mạch.</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"https://tamduchearthospital.com/wp-content/uploads/2023/04/DSCF3691-300x200.jpg\" width=\"368\" height=\"245\"></p>');

